import React, { useState } from 'react'
import { connect } from 'react-redux'
import { editIntro } from '../actions/introActions'

const EditIntro = ({imageURL, description, dispatchEditIntro }) => {
	const [inputImageURL, setInputImageURL] = useState('');
	const [inputDescription, setInputDescription] = useState('')
	const [editing, setEditing] = useState(true);

	console.log(`dispatchEditIntro: ${dispatchEditIntro}`);

	const getDisplayType = (inputDiv) => {
		if (inputDiv) {
			if (editing) return {display:'inline-block'}
			return {display:'none'}
		} else {
			if (editing) return {display:'none'}
			return {display:'inline-block'}
		}
	}

	const saveInfo = () => {
		dispatchEditIntro(inputImageURL, inputDescription);		
		setEditing(false)
	}

	return (
		<div className='Intro'>
			<div className='IntroInput' style={getDisplayType(true)}>
				<div>
					<label htmlFor="inputImageURL">Image</label>
					<input onChange={e => setInputImageURL(e.target.value) } id="inputImageURL" name="inputImageURL"/>		
				</div>
				<div>
					<label htmlFor="inputDescription">Description</label>	
					<input onChange={e => setInputDescription(e.target.value)} id="inputDescription" name="inputDescription"/>
				</div>
				<button onClick={saveInfo}>Save</button>
				<button onClick={() => setEditing(false)}>Cancel</button>
			</div>
			<div className='IntroDisplay' style={getDisplayType(false)}>
				<img src={props.imageURL}/>
				<div>{props.description}</div> 
				<button onClick={() => setEditing(true)}>Edit</button>
			</div>
		</div>
	)
}

const mapDispatchToProps = dispatch => ({
  dispatchEditIntro: (imageURL, description) => dispatch(editIntro(imageURL, description))
})

const mapStateToProps = state => { 
	return {
		imageURL: state.imageURL,
		description: state.description	
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(EditIntro)
